import{_ as e,c as a,o,aa as r}from"./chunks/framework.D7pbs6VA.js";const d="/yuyou-notes/assets/vim_model.KEGR0W_Z.png",_=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"编程学习/Linux/001-vim.md","filePath":"编程学习/Linux/001-vim.md","lastUpdated":1741439126000}'),c={name:"编程学习/Linux/001-vim.md"},n=r('<h2 id="三种模式" tabindex="-1">三种模式 <a class="header-anchor" href="#三种模式" aria-label="Permalink to &quot;三种模式&quot;">​</a></h2><h3 id="命令模式-normal" tabindex="-1">命令模式/normal <a class="header-anchor" href="#命令模式-normal" aria-label="Permalink to &quot;命令模式/normal&quot;">​</a></h3><h5 id="移动光标" tabindex="-1">移动光标 <a class="header-anchor" href="#移动光标" aria-label="Permalink to &quot;移动光标&quot;">​</a></h5><pre><code>`h` `j` `k` `l` 控制光标移动（前/下/上/后）\n`^` /`$` 行首/行尾\n`w`向后移动一个单词，到下一个单词开头\n`e`向后移动一个单词，到当前单词的结尾\n`0`行首\n</code></pre><p><code>yy</code> 复制内容 <code>p</code> 粘贴内容</p><h5 id="删除" tabindex="-1">删除 <a class="header-anchor" href="#删除" aria-label="Permalink to &quot;删除&quot;">​</a></h5><pre><code>`dd` 删除整行内容\n`x` 删除单个字\n`dw`删除单词\n`d$`删除从光标到行末\n</code></pre><h5 id="撤销" tabindex="-1">撤销 <a class="header-anchor" href="#撤销" aria-label="Permalink to &quot;撤销&quot;">​</a></h5><pre><code>`u`  (undo)撤销\n`U`撤销整行的修改\n`ctrl+R`取消撤销\n</code></pre><h5 id="替换" tabindex="-1">替换 <a class="header-anchor" href="#替换" aria-label="Permalink to &quot;替换&quot;">​</a></h5><pre><code>`r + 要替换的内容`\n</code></pre><h5 id="更改" tabindex="-1">更改 <a class="header-anchor" href="#更改" aria-label="Permalink to &quot;更改&quot;">​</a></h5><pre><code>`cw`删除当前光标后面的单词，并进入插入模式\n</code></pre><h5 id="快速移动行" tabindex="-1">快速移动行 <a class="header-anchor" href="#快速移动行" aria-label="Permalink to &quot;快速移动行&quot;">​</a></h5><pre><code>`ctrl + g`显示文件信息及行号\n`G` 最后一行\n`gg` 第一行\n`100G`  / `：100` 跳转到第一百行\n`ctrl + O`回到上次的行\n`ctrl + I`回到最新的行\n</code></pre><h5 id="查找" tabindex="-1">查找 <a class="header-anchor" href="#查找" aria-label="Permalink to &quot;查找&quot;">​</a></h5><pre><code>`/要查找的内容`  向下查找 \n`？要查找的内容`  向上查找 \n`/hello\\c`  / `：set ic` 不区分大小写查找 \n</code></pre><h5 id="括号的匹配" tabindex="-1">括号的匹配 <a class="header-anchor" href="#括号的匹配" aria-label="Permalink to &quot;括号的匹配&quot;">​</a></h5><pre><code>`%`\n</code></pre><h5 id="v可视化" tabindex="-1">V可视化 <a class="header-anchor" href="#v可视化" aria-label="Permalink to &quot;V可视化&quot;">​</a></h5><pre><code>v选择\nV行选择\n^v块选择\n</code></pre><h5 id="改变大小写" tabindex="-1">改变大小写 <a class="header-anchor" href="#改变大小写" aria-label="Permalink to &quot;改变大小写&quot;">​</a></h5><pre><code>u变小写\nU变大写\n～反转大小写\n</code></pre><p><code>Ctrl</code> + <code>f</code> 向前翻页 <code>Ctrl</code> + <code>b</code> 向后翻页 <code>Ctrl</code> + <code>u</code> 向上翻半页 <code>Ctrl</code> + <code>d</code> 向下翻半页 <code>vi .vimrc</code> 设置默认操作</p><h3 id="插入模式" tabindex="-1">插入模式 <a class="header-anchor" href="#插入模式" aria-label="Permalink to &quot;插入模式&quot;">​</a></h3><p>iI：光标前/行首 aA：光标后/行尾 oO:上一行/下一行</p><h3 id="尾行模式" tabindex="-1">尾行模式 <a class="header-anchor" href="#尾行模式" aria-label="Permalink to &quot;尾行模式&quot;">​</a></h3><p><code>：set number</code> / <code>：set nu</code> 显示行号 <code>：set nonumber</code> 关闭行号</p><h5 id="替换-1" tabindex="-1">替换 <a class="header-anchor" href="#替换-1" aria-label="Permalink to &quot;替换&quot;">​</a></h5><pre><code>`:1,5s/要替换的内容/替换后的内容/g` 替换1到5行 `g` 全局替换\n`:%1,$%s/要替换的内容/替换后的内容/g` 全部替换\n`:%1,$%s/要替换的内容/替换后的内容/gc` 对每一个提示是否替换\n</code></pre><p><code>：r filename</code>写入文件 <code>：w filename</code>存入文件 <code>sp</code>分屏 <code>vsp</code>分屏 <code>^w w</code>切换窗口 <code>^w r</code>交换窗口 <code>^w c</code>关闭窗口</p><p><img src="'+d+'" alt=""></p>',32),t=[n];function i(l,h,s,p,u,m){return o(),a("div",null,t)}const q=e(c,[["render",i]]);export{_ as __pageData,q as default};
